export default function searchParameters(socialMedia, product, searchTerm) {
  console.log("step 1 complete!!", { socialMedia, product, searchTerm });
  switch (socialMedia) {
    case "twitter":
      console.log("step 3 complete!!!", twitterSearchParameters(product, searchTerm));
      break;
    case "reddit":
      redditSearchParameters(product, searchTerm);
      break;
    case "facebook":
      break;
  }
}

