import React from "react"
import { Dropdown, DropdownButton } from 'react-bootstrap'
import { products } from './Home';
import { useDispatch, useSelector } from "react-redux";
import "./../styles.css";

function DropdownMenu ({product}) {
    const dispatchRedux = useDispatch();
    const selectedProduct = useSelector(appState => appState.selectedProduct);

    return (
        <DropdownButton alignRight id="nav-dropdown" title={product}>
            {products.map(({product}, index) => (
                <Dropdown.Item onSelect={e => dispatchRedux({ type: "SELECTED_PRODUCT", payload: {product}})} href="#" key={index}>{product}</Dropdown.Item>
            ))}
        </DropdownButton>
    );
}

export default DropdownMenu;
