import React from "react";
import { TwitterTweetEmbed } from "react-twitter-embed";

function RetreiveAPI(props) {
  console.log("RetreiveAPI props", props);
  //To Do: 1/11
  // If (twitter) Retreive Twitter API using useEffect
  // If (facebook) Retreive FB API using useEffect
  // If (reddit) Retreive Reddit API using useEffect

  //To Do: Hook up search keyword to initial state
  const initialState = {
    searchParam: {twitter: 'Peloton', reddit: 'Bike'},
    retreivedTweets: []
  };
  const [receivedTweets, receivedTweetsDispatch] = React.useReducer(reducer, initialState.retreivedTweets);

  const getTweet = async() => {
    const response = await fetch('/twitter');
    const incomingData = await response.json();
    receivedTweetsDispatch({ type: "RECEIVED_TWEETS", payload: incomingData.statuses });
  };

  const getRedditPost = async() => {
    const neededInfo = { 
      title: "",
      text: "",
      author: "",
      createdTime: {}
    }
    let newArray = [];
    const response = await fetch('/reddit');
    const incomingData = await response.json();

    let redditData = incomingData.map((post, index) => {
      newArray.push({ 
        title: post.title,
        text: post.selftext,
        author: post.author,
        createdTime: post.created_utc
      })
    })
    console.log('wtf', redditData)

    receivedTweetsDispatch({ type: "RECEIVED_REDDIT_POSTS", payload: redditData })
  }

  const sendSearchParamTwitter = () => {
    fetch('/twitterSearchParam', {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({seachParam: initialState.searchParam.twitter})
    });
    getTweet()
  }

  const sendSearchParamReddit = (e) => {
    fetch('/redditSearchParam', {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({searchParam: initialState.searchParam.reddit})
    });
    getRedditPost();
  }

  function reducer(prevState, {type, payload}) {
    console.log('tweet reducer payload', payload);
    //may need to add let newReceivedTweets = []
    //see from hw due 1/14 - refactor to redux
    switch(type) {
      case "RECEIVED_TWEETS":
        const newReceivedTweets = [...prevState];
        newReceivedTweets.splice(0, newReceivedTweets.length, ...payload);
        return newReceivedTweets;
      case "RECEIVED_REDDIT_POSTS":
        const newReceivedRedditPosts = [...prevState];
        console.log('taylor you made it here!!', newReceivedRedditPosts);

      default:
        return prevState;
    }
  }

  React.useEffect(() => {
    if(props.match.params.socialMedia === 'twitter') sendSearchParamTwitter();
    if(props.match.params.socialMedia === 'reddit') sendSearchParamReddit();
  }, []);

  return (
    <div>
      Showing {props.match.params.socialMedia} Member posts for {props.match.params.product}!!!
      <p>
      <button onClick={sendSearchParamReddit}>Button to Get Tweets! </button>
      </p>
      {receivedTweets.map(receivedTweet => (
        <TwitterTweetEmbed tweetId={receivedTweet.id_str} key={receivedTweet.id}/>
      ))}
    </div>
  );
}

export default RetreiveAPI;
