import React from "react";
import { TwitterTweetEmbed } from "react-twitter-embed";

function RetreiveAPI(props) {
  console.log("RetreiveAPI props", props);
  //To Do: 1/11
  // If (twitter) Retreive Twitter API using useEffect
  // If (facebook) Retreive FB API using useEffect
  // If (reddit) Retreive Reddit API using useEffect

  //To Do: Hook up search keyword to initial state
  const initialState = {
    searchParam: 'Peloton',
    retreivedTweets: []
  };

  const getTweet = async() => {
    console.log('made it to get Tweet!');

    const response = await fetch('/twitter');
    const incomingData = await response.json();
    console.log('Client: I did it! Tweet fetched: ', incomingData);
    receivedTweetDispatch({ type: "RECEIVED_TWEETS", payload: incomingData.statuses });
  };

  const sendSearchParam = e => {
    e.preventDefault();
    fetch('/userName', {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({userName: initialState.searchParam})
    });
    getTweet()
  }

  function reducer(prevState, {type, payload}) {
    console.log('tweet reducer payload', payload);
    switch(type) {
      case "RECEIVED_TWEETS":
        const newTweetArray = [...prevState];
        return null;
      default:
        return prevState;
    }
  }

  const [receivedTweet, receivedTweetDispatch] = React.useReducer(reducer, initialState.retreivedTweets);

  return (
    <div>
      Showing {props.match.params.socialMedia} Member posts for {props.match.params.product}!!!
      <p>
      <button onClick={sendSearchParam}>Button to Get Tweets! </button>
      </p>
    </div>
  );
}

export default RetreiveAPI;
