import React from "react";
import { useSelector } from "react-redux";
import Twitter from "./Twitter";
import Reddit from "./Reddit";
import Facebook from "./Facebook";
import styled from "styled-components";
import EmptySearchResult from "./EmptySearchResult";
import searchParameters from "./GetSearchQuery";
import { sendSearchParamTwitter, sendSearchParamReddit } from "./SendSearchQueryToApi";

function reducer(prevState, { type, payload }) {
  console.log("tweet reducer payload", payload);
  let newPosts = [];
  switch (type) {
    case "RECEIVED_TWEETS":
      newPosts = [...prevState];
      newPosts.splice(0, newPosts.length, ...payload);
      return newPosts;
    case "RECEIVED_REDDIT_POSTS":
      newPosts = [...prevState];
      newPosts.splice(0, newPosts.length, ...payload);
      return newPosts;
    default:
      return prevState;
  }
}

function RetreiveAPI(props) {
  console.log("RetreiveAPI props", props);
  const [receivedPosts, receivedPostsDispatch] = React.useReducer(reducer, [""]);
  const { selectedProduct, selectedSocialMedia, searchTerm } = useSelector(appState => appState);

  const getTweet = async searchParameter => {
    sendSearchParamTwitter(searchParameter);
    const response = await fetch("/twitter");
    const incomingData = await response.json();
    receivedPostsDispatch({
      type: "RECEIVED_TWEETS",
      payload: incomingData.statuses
    });
  };

  const getRedditPost = async searchParameter => {
    let newArray = [];
    sendSearchParamReddit(searchParameter);
    const response = await fetch("/reddit");
    const incomingData = await response.json();
    incomingData.map(post => {
      newArray.push({
        title: post.title,
        text: post.selftext,
        author: post.author,
        createdTime: post.created_utc,
        url: post.permalink
      });
    });
    receivedPostsDispatch({ type: "RECEIVED_REDDIT_POSTS", payload: newArray });
  };

  React.useEffect(() => {
    console.log("in useEffect!", { selectedProduct, selectedSocialMedia, searchTerm });
    let socialMediaSearchParameter = searchParameters(selectedSocialMedia, selectedProduct, searchTerm);
    console.log("Finalnly did it!!!", socialMediaSearchParameter);
    if (selectedSocialMedia === "twitter") getTweet(socialMediaSearchParameter);
    if (selectedSocialMedia === "reddit") getRedditPost(socialMediaSearchParameter);
  }, [selectedProduct]);

  const memberPosts = () => {
    console.log("neeeeeeed results!!!!", receivedPosts);
    if (receivedPosts.length > 0) {
      switch (selectedSocialMedia) {
        case "twitter":
          return receivedPosts.map(receivedPost => <Twitter tweetId={receivedPost.id_str} key={receivedPost.id} />);
        case "reddit":
          return receivedPosts.map(receivedPost => <Reddit post={receivedPost} />);
        case "facebook":
          return <Facebook />;
      }
    } else return <EmptySearchResult />;
  };

  return (
    <div>
      <div align="center">
        Showing {selectedSocialMedia} posts for {props.match.params.product}
      </div>
      <div>
        <PostContainer>{memberPosts()}</PostContainer>
      </div>
    </div>
  );
}

const PostContainer = styled.div`
  list-style-type: none;
  margin-left: 70px;
  margin-right: 70px;
`;

export default RetreiveAPI;
