import React from "react";
import { withRouter } from "react-router";
import Flippy, { FrontSide, BackSide } from "react-flippy";
import { SocialIcon } from "react-social-icons";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import classnames from "classnames";

const socialMediaList = ["twitter", "facebook", "reddit"];
const initialState = {
  isFlipped: false,
};

function reducer(prevState, { type, payload }) {
  switch (type) {
    case "CARD_FLIPPED":
      return !prevState;
    default:
      return prevState;
  }
}

function Card({ product, image, history }) {
  const dispatchRedux = useDispatch();
  const colorSelected = useSelector(appState => appState.selectedProduct);
  const hideCard = colorSelected !== "All" && colorSelected !== product;
  const [flippedState, flippedDispatch] = React.useReducer(
    reducer,
    initialState.isFlipped
  );

  const goToRetreiveAPI = (history, product, socialMedia) => {
    let pathname = `/${product}/${socialMedia}`;
    dispatchRedux({type: "SELECTED_PRODUCT", payload: { product, socialMedia } })
    history.push(pathname);
  };

  console.log("product clicked", product);
  return (
    <div align="center" className={classnames("col-lg-3 col-md-4 col-12 mb-5 animated fadeIn", {"d-none": hideCard})}>
    <div onClick={() => flippedDispatch({ type: "CARD_FLIPPED" })} >
    <Image src={image} />
    <Flippy isFlipped={flippedState}>
      <FrontSide  style={{backgroundColor: "white"}} >
        <div className="card-body">
          <h4 className="card-title">{product}</h4>
        </div>
      </FrontSide>
      <BackSide style={{backgroundColor: "white"}}>
        <div onMouseLeave={() => flippedDispatch({ type: "CARD_FLIPPED" })} className="row justify-content-around h-100 d-flex justify-content-center align-items-center">
          {socialMediaList.map((socialMedia, index) => (
            <SocialIcon
              network={socialMedia}
              fgColor="white"
              onClick={() => goToRetreiveAPI(history, product, socialMedia)}
              key={index}
            />
          ))}
        </div>
      </BackSide>
    </Flippy>
    </div>
    </div>
  );
}

const Image = styled.img`
  height: 100%;
  width: 100%;
  object-fit: contain;
`;

export default withRouter(Card);
