import React from "react";
import Card from "./Card";
import styled from "styled-components";

const products = ["Bike", "Tread", "iOS", "Apple Watch", "Android", "FireTV", "Members"];

function Home() {
  return (
    <>
      <h1 align="center">Welcome to Peloton Members' Feedback App!</h1>
      <PageContainer>
      {/* <main style={{ padding: "20px" }} className="container"> */}
        <div className="mx-auto">
          <CardContainer>
          {products.map((product, index) => (
            <Card product={product} key={index} />
          ))}
          </CardContainer>
        </div>
        </PageContainer>
      {/* </main> */}
    </>
  );
};

const PageContainer = styled.main`
  flex: 1 0 auto;
  padding: 40px;
  class-name: container;
`;

const CardContainer = styled.ul`
  margin: auto;
  display: flex;
  flex-wrap: wrap;
`

export default Home;
