import React from "react";
import { withRouter } from "react-router";
import { Dropdown, DropdownButton } from 'react-bootstrap'
import { SocialIcon } from "react-social-icons";
import { socialMediaList } from "./Card"

function SearchBar() {
    const [searchSocialMedia, setSearchSocialMedia] = React.useState("");

    const selectHandler = (socialMedia) => {
        console.log('in state!!', socialMedia)
        setSearchSocialMedia(prevCount => {
        const selectedSocialMedia = socialMedia;
        return selectedSocialMedia;
        })
    }
    console.log('searchSocialMedia', searchSocialMedia);
    return (
        <div className="form-group">
            <div className="input-group mb-3">
            <DropdownButton drop='down'
                title={
                    <SocialIcon
                        network = {searchSocialMedia}
                        fgColor="white"
                        bgColor="gray"
                        style={{ height: 25, width: 25 }}
                    />
                }
            >
                {socialMediaList.map((socialMedia, index) => (
                    <Dropdown.Item onSelect={() => setSearchSocialMedia(prevState => socialMedia)}
                    >
                        <SocialIcon
                            network={socialMedia}
                            fgColor="white"
                            key={index}
                    />
                    </Dropdown.Item>
                ))}
            </DropdownButton>
                <form className="form-inline my-2 my-lg-0">
                    <input className="form-control mr-sm-2" type="text" placeholder="Search"/>
                    <button className="btn btn-secondary my-2 my-sm-0" type="submit">Search</button>
                </form>
            </div>
        </div>
    );
}

export default withRouter(SearchBar);
