import React from "react"
import { Dropdown, DropdownButton } from 'react-bootstrap'
import { products } from './Home';
import "./../styles.css";

function DropdownMenu () {
    const [buttonText, setButtonText] = React.useState('All');

    const clickHandler = (product) => setButtonText(prevState => prevState = product);

    return (
        <DropdownButton alignRight id="nav-dropdown" title={buttonText}>
            {products.map(product => (
                <Dropdown.Item onClick={() => clickHandler(product)} href="#/action-1">{product}</Dropdown.Item>
            ))}
        </DropdownButton>
    );
}

export default DropdownMenu;
