import React from "react"
import { Dropdown, DropdownButton } from 'react-bootstrap'
import { withRouter } from "react-router";
import { products } from './Home';
import { useDispatch } from "react-redux";
import "./../styles.css";

function DropdownMenu ({product, socialMedia, history}) {
    const dispatchRedux = useDispatch();

    const onSelectHandler = (history, product, socialMedia) => {
        let pathname = `/${product}/${socialMedia}`;
        dispatchRedux({ type: "SELECTED_PRODUCT", payload: {product, socialMedia}});
        history.push(pathname);
    }

    return (
        <DropdownButton alignRight id="nav-dropdown" title={product}>
            {products.map(({product}, index) => (
                <Dropdown.Item onSelect={() => onSelectHandler(history, product, socialMedia)} key={index}>{product}</Dropdown.Item>
            ))}
        </DropdownButton>
    );
}

export default withRouter(DropdownMenu);
