import React from "react";
import { withRouter } from "react-router";
import { useDispatch, useSelector } from "react-redux";

import { Dropdown, DropdownButton } from 'react-bootstrap'
import { SocialIcon } from "react-social-icons";
import { socialMediaList } from "./Card"

function SearchBar({history}) {
    const dispatchRedux = useDispatch();
    const [searchSocialMedia, setSearchSocialMedia] = React.useState("");
    const [{ searchText }, dispatchFormState] = React.useState({ searchText: "" });
    const isEnabled = (searchSocialMedia !== "") && (searchText !== "");

    console.log('Please!!!!', isEnabled);

    const updateForm = e => {
        let searchText = e.target.value;
        dispatchFormState(prevState => {
            const newState = {...prevState, searchText: searchText};
            return newState;
        })
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        let pathname = `/${searchText}/${searchSocialMedia}`;
        dispatchRedux({type: "SELECTED_PRODUCT", payload: {searchText, searchSocialMedia}});
        history.push(pathname);
    }

    console.log('searchSocialMedia', searchSocialMedia);
    return (
        <div className="form-group">
            <div className="input-group mb-3">
            <DropdownButton drop='down'
                title={
                    <SocialIcon
                        network = {searchSocialMedia}
                        fgColor="white"
                        bgColor="gray"
                        style={{ height: 25, width: 25 }}
                    />}
            >
                {socialMediaList.map((socialMedia, index) => (
                    <Dropdown.Item onSelect={() => setSearchSocialMedia(prevState => socialMedia)}
                    >
                        <SocialIcon
                            network={socialMedia}
                            fgColor="white"
                            key={index}
                    />
                    </Dropdown.Item>
                ))}
            </DropdownButton>
                <form className="form-inline my-2 my-lg-0">
                    <input 
                        className="form-control mr-sm-2"
                        type="text"
                        name="text"
                        placeholder="Search"
                        value={searchText}
                        onChange={updateForm} />
                    <button 
                        className="btn btn-secondary my-2 my-sm-0"
                        type="submit"
                        onClick={handleSubmit}
                        disabled={!isEnabled}
                    >
                        Search
                    </button>
                </form>
            </div>
        </div>
    );
}

export default withRouter(SearchBar);
