import React from "react";
import { withRouter } from "react-router";
import Flippy, { FrontSide, BackSide } from "react-flippy";
import { SocialIcon } from "react-social-icons";
import styled from "styled-components";

const socialMediaList = ["twitter", "facebook", "facebook"];
const initialState = {
  isFlipped: false,
};

function reducer(prevState, { type, payload }) {
  switch (type) {
    case "CARD_FLIPPED":
      return !prevState;
    default:
      return prevState;
  }
}

const goToRetreiveAPI = (history, product, socialMedia) => {
  let pathname = `/${product}/${socialMedia}`;
  history.push(pathname);
};

function Card({ product, history }) {
  const [flippedState, flippedDispatch] = React.useReducer(
    reducer,
    initialState.isFlipped
  );

  console.log("product clicked", product);
  return (

    <div align="center" className="col-lg-3 col-md-4 col-12 animated fadeIn">
    <Flippy isFlipped={flippedState}>
      <FrontSide onClick={() => flippedDispatch({ type: "CARD_FLIPPED" })}>
        <div className="card-body">
          <p className="card-text">Insert photo here</p>
          <h4 className="card-title">{product}</h4>
        </div>
      </FrontSide>
      <BackSide onMouseLeave={() => flippedDispatch({ type: "CARD_FLIPPED" })}>
        <div className="row justify-content-around h-100 d-flex justify-content-center align-items-center">
          {socialMediaList.map((socialMedia, index) => (
            <SocialIcon
              network={socialMedia}
              onClick={() => goToRetreiveAPI(history, product, socialMedia)}
              key={index}
            />
          ))}
        </div>
      </BackSide>
    </Flippy>
    </div>
  );
}

// const Container = styled.div`
//   background-color: white;
// `;

export default withRouter(Card);
