import { connectableObservableDescriptor } from "rxjs/internal/observable/ConnectableObservable";

const initialState = {
  selectedProduct: "All",
  selectedSocialMedia: "",
  searchTerm: ""
};

export default (prevState = initialState, { type, payload }) => {
  console.log("in redux!!!!", { type, payload });
  let todos = [];
  let searchTerms = "";
  switch (type) {
    case "SELECTED_PRODUCT":
      console.log("SELECTED_PRODUCT!!!!", payload);
      switch (payload.product) {
        case "Bike":
          searchTerms = "Peloton Bike";
          break;
        case "Tread":
          searchTerms = "Peloton Tread";
          break;
        case "iOS":
          searchTerms = "Peloton iOS App";
          break;
        case "Apple Watch":
          searchTerms = "Peloton Apple Watch App";
          break;
        case "Android":
          searchTerms = "Peloton Android App";
          break;
        case "FireTV":
          searchTerms = "Peloton FireTV";
          break;
        case "Members":
          searchTerms = "Peloton Website";
          break;
        default:
          searchTerms = payload.searchText;
          break;
      }
      return {
        ...prevState,
        selectedProduct: payload.product,
        selectedSocialMedia: payload.socialMedia,
        searchTerm: searchTerms
      };
    case "RESET":
      return { ...prevState, selectedProduct: "All" };
    case "addTodo":
      if (!prevState.inputValue) return { ...prevState };
      const newTodo = { name: prevState.inputValue, done: false };
      break;

    // return { todos: [...prevState.todos, newTodo], inputValue: "" };
    case "completeToggle":
      todos = [...prevState.todos];
      todos[payload].done = !todos[payload].done;
      break;

    // return { ...prevState, todos };
    case "removeTodo":
      todos = [...prevState.todos];
      todos.splice(payload, 1);
      // return { ...prevState, todos };
      break;

    default:
      return { ...prevState };
  }
};
